'use strict';

let fsPath = require('path');
let readFileSync = require('fs').readFileSync;
let readdirSync = require('fs').readdirSync;
let statSync = require('fs').statSync;
let Container = require('./container');
let Config = require('./config');
let event = require('../lib/event');
let glob = require('glob');
let fileExists = require('./utils').fileExists;
let runHook = require('./hooks');

/**
 * CodeceptJS runner
 */
class Codecept {


  /**
   * Create CodeceptJS runner.
   * Config and options should be passed
   *
   * @param {*} config
   * @param {*} opts
   */
  constructor(config, opts) {
    this.config = config;
    this.opts = opts;
    this.testFiles = [];
  }

  /**
   * Initialize CodeceptJS at specific directory.
   * If async initialization is required pass callbacke as second parameter.
   *
   * @param {*} dir
   * @param {*} callback
   */
  init(dir, callback) {
    // preparing globals
    global.codecept_dir = dir;
    global.output_dir = fsPath.resolve(dir, this.config.output);
    global.actor = global.codecept_actor = require('./actor');
    global.Helper = global.codecept_helper = require('./helper');
    global.pause = require('./pause');
    global.within = require('./within');

    // initializing listeners
    Container.create(this.config, this.opts);
    this.bootstrap(callback);
  }

  /**
   * Executes hooks and bootstrap.
   * If bootstrap is async second parameter is required.
   *
   * @param {*} done
   */
  bootstrap(done) {

    // default hooks
    runHook(require('./listener/steps'));
    runHook(require('./listener/helpers'));
    runHook(require('./listener/exit'));
    runHook(require('./listener/trace'));

    // custom hooks
    this.config.hooks.forEach((hook) => runHook(hook));

    // bootstrap
    runHook(this.config.bootstrap, done, 'bootstrap');
  }

  /**
   * Executes teardown.
   * If teardown is async a parameter is provided.
   *
   * @param {*} done
   */
  teardown(done) {
    runHook(this.config.teardown, done, 'teardown');
  }

  /**
   * Loads tests by pattern or by config.tests
   *
   * @param {optional} pattern
   */
  loadTests(pattern) {
    pattern = pattern || this.config.tests;
    glob.sync(fsPath.resolve(codecept_dir, pattern)).forEach((file) => {
      this.testFiles.push(fsPath.resolve(file));
    });
  }

  /**
   * Run a specific test or all loaded tests.
   *
   * @param {optional} test
   */
  run(test) {
    let mocha = Container.mocha();
    mocha.files = this.testFiles;
    if (test) {
      mocha.files = mocha.files.filter((t) => fsPath.basename(t, '_test.js') === test || fsPath.basename(t, '.js') === test || fsPath.basename(t) === test);
    }
    mocha.run().on('end', () => {
      let done = () => {
        event.emit(event.all.result, this);
      };
      this.teardown(done);
    });
  }


  static version() {
    return JSON.parse(readFileSync(__dirname + '/../package.json', 'utf8')).version;
  }
}

module.exports = Codecept;

'use strict';
let path = require('path');
let fileExists = require('./utils').fileExists;
let Translation = require('./translation');
let MochaFactory = require('./mocha_factory');

let container = {
  helpers: {},
  support: {},
  mocha: {},
  translation: {},
};

/**
 * Dependency Injection Container
 */
class Container {

  /**
   * Create container with all required helpers and support objects
   *
   * @api
   * @param {*} config
   * @param {*} opts
   */
  static create(config, opts) {
    container.mocha = MochaFactory.create(config.mocha || {}, opts || {});
    container.helpers = createHelpers(config.helpers || {});
    container.translation = loadTranslation(config.translation || null);
    container.support = createSupportObjects(config.include || {});
  }

  /**
   * Get all support objects or get support object by name
   *
   * @api
   * @param {optional} name
   */
  static support(name) {
    if (!name) {
      return container.support;
    }
    return container.support[name];
  }

  /**
   * Get all helpers or get a helper by name
   *
   * @api
   * @param {optional} name
   */
  static helpers(name) {
    if (!name) {
      return container.helpers;
    }
    return container.helpers[name];
  }

  /**
   * Get translation
   *
   * @api
   */
  static translation() {
    return container.translation;
  }

  /**
   * Get Mocha instance
   *
   * @api
   */
  static mocha() {
    return container.mocha;
  }

  /**
   * Append new services to container
   *
   * @api
   */
  static append(newContainer) {
    const deepMerge = require('./utils').deepMerge;
    container = deepMerge(container, newContainer);
  }

  /**
   * Clear container
   *
   * @param {*} newHelpers
   * @param {*} newSupport
   */
  static clear(newHelpers, newSupport) {
    container.helpers = newHelpers || {};
    container.support = newSupport || {};
    container.translation = loadTranslation();
  }
}

module.exports = Container;

function createHelpers(config) {
  let helpers = {};
  let helperModule;
  for (let helperName in config) {
    try {
      module = config[helperName].require
        ? path.resolve(global.codecept_dir, config[helperName].require) // custom helper
        : './helper/' + helperName; // built-in helper
      let HelperClass = require(module);
      if (HelperClass._checkRequirements) {
        let requirements = HelperClass._checkRequirements();
        if (requirements) {
          let install;
          if (require('./utils').installedLocally()) {
            install = "npm install --save-dev " + requirements.join(' ');
          } else {
            install = "[sudo] npm install -g " + requirements.join(' ');
          }
          throw new Error("Required modules are not installed.\n\nRUN: " + install);
        }
      }
      helpers[helperName] = new HelperClass(config[helperName]);
    } catch (err) {
      throw new Error(`Could not load helper ${helperName} from module '${module}':\n${err.message}\n${JSON.stringify(err)}`);
    }
  }

  for (let name in helpers) {
    if (helpers[name]._init) helpers[name]._init();
  }
  return helpers;
}

function createSupportObjects(config) {
  let objects = {};
  for (let name in config) {
    let module = config[name];
    if (module.charAt(0) === '.') {
      module = path.join(global.codecept_dir, module);
    }
    try {
      objects[name] = require(module);
    } catch (err) {
      throw new Error(`Could not include object ${name} from module '${module}'\n${err.message}`);
    }
    try {
      if (typeof objects[name] === 'function') {
        objects[name] = objects[name]();
      } else if (objects[name]._init) {
        objects[name]._init();
      }
    } catch (err) {
      throw new Error(`Initialization failed for ${objects[name]}\n${err.message}`);
    }
  }
  if (!objects.I) {
    objects.I = require('./actor')();

    if (container.translation.I != 'I') {
      objects[container.translation.I] = objects.I;
    }
  }

  return objects;
}

function loadTranslation(translation) {
  if (!translation) {
    return new Translation({
      I: 'I',
      actions: {}
    }, false);
  }

  let vocabulary;
  // check if it is a known translation
  if (require('../translations')[translation]) {
    vocabulary = require('../translations')[translation];
    return new Translation(vocabulary);
  } else if (fileExists(path.join(global.codecept_dir, translation))) {
    // get from a provided file instead
    vocabulary = require(path.join(global.codecept_dir, translation));
  } else {
    throw new Error(`Translation option is set in config, but ${translation} is not a translated locale or filename`);
  }

  return new Translation(vocabulary);
}

var fs = require('fs');
var path = require('path');
var getParameterNames = require('get-parameter-names');

function isObject(item) {
  return item && typeof item === 'object' && !Array.isArray(item);
}

function deepMerge(target, source) {
  if (isObject(target) && isObject(source)) {
    for (const key in source) {
      if (isObject(source[key])) {
        if (!target[key]) Object.assign(target, { [key]: {} });
        deepMerge(target[key], source[key]);
      } else {
        Object.assign(target, { [key]: source[key] });
      }
    }
  }
  return target;
}

module.exports.deepMerge = deepMerge;

module.exports.fileExists = function (filePath) {
  try {
    fs.statSync(filePath);
  } catch (err) {
    if (err.code === 'ENOENT') return false;
  }
  return true;
};

module.exports.isFile = function (filePath) {
  var filestat;
  try {
    filestat = fs.statSync(filePath);
  } catch (err) {
    if (err.code === 'ENOENT') return false;
  }
  if (!filestat) return false;
  return filestat.isFile();
};

module.exports.getParamNames = function (fn) {
  if (fn.isSinonProxy) return [];
  return getParameterNames(fn);
};

module.exports.installedLocally = function () {
  return path.resolve(__dirname + '/../').indexOf(process.cwd()) === 0;
};

module.exports.methodsOfObject = function (obj, className) {
  var methods = [];

  const standard = [
    'constructor',
    'toString',
    'toLocaleString',
    'valueOf',
    'hasOwnProperty',
    'isPrototypeOf',
    'propertyIsEnumerable'
  ];

  while (obj.constructor.name !== className) {
    Object.getOwnPropertyNames(obj).forEach((prop) => {
      if (typeof obj[prop] !== 'function') return;
      if (standard.indexOf(prop) >= 0) return;
      if (prop.indexOf('_') === 0) return;
      methods.push(prop);
    });
    obj = obj.__proto__;

    if (!obj || !obj.constructor) break;
  }
  return methods;
};

module.exports.template = function (template, data) {
  return template.replace(/{{([^{}]*)}}/g, function (a, b) {
    var r = data[b];
    if (r === undefined) return '';
    return r.toString();
  });
};

module.exports.ucfirst = function (str) {
  return str.charAt(0).toUpperCase() + str.substr(1);
};

module.exports.lcfirst = function (str) {
  return str.charAt(0).toLowerCase() + str.substr(1);
};

module.exports.hashCode = function (str) {
  /* Getting 32bit hashCode of string like in Java.
   *  Used in Screenshot name to provide short screenshot names
   */
  var hash = 0;
  var char;
  if (str.length == 0) return hash;
  for (var i = 0; i < str.length; i++) {
    char = str.charCodeAt(i);
    hash = (hash << 5) - hash + char;
    hash = hash & hash; // Convert to 32bit integer
  }
  return hash;
};

module.exports.xpathLocator = {
  literal: (string) => {
    if (string.indexOf("'") > -1) {
      string = string.split("'", -1).map(function (substr) {
        return "'" + substr + "'";
      }).join(',"\'",');
      return "concat(" + string + ")";
    } else {
      return "'" + string + "'";
    }
  },
  combine: (locators) => {
    return locators.join(' | ');
  }
};

module.exports.test = {

  submittedData: function (dataFile) {
    return function (key) {
      var data = JSON.parse(fs.readFileSync(dataFile, 'utf8'));
      if (key) {
        return data.form[key];
      }
      return data;
    };
  },

  expectError: function () {
    throw new Error('should not be thrown');
  }
};

'use strict';

class Translation {

  constructor(vocabulary, loaded) {
    this.vocabulary = vocabulary;
    this.loaded = loaded !== false;
  }

  value(val) {
    return this.vocabulary[val];
  }

  actionAliasFor(actualActionName) {
    if (this.vocabulary.actions && this.vocabulary.actions[actualActionName]) {
      return this.vocabulary.actions[actualActionName];
    }
    return actualActionName;
  }

  get I() {
    return this.vocabulary.I;
  }
}

module.exports = Translation;

const Mocha = require('mocha/lib/mocha');
const fsPath = require('path');
const reporter = require('./reporter/cli');
const scenarioUi = fsPath.join(__dirname, './interfaces/bdd.js');

let mocha;

class MochaFactory {

  static create(config, opts) {
    mocha = new Mocha(Object.assign(config, opts));

    mocha.ui(scenarioUi);
    // use standard reporter
    if (!opts.reporter) {
      mocha.reporter(reporter, opts);
      return mocha;
    }

    // load custom reporter with options
    var reporterOptions = Object.assign(config.reporterOptions || {});

    if (opts.reporterOptions !== undefined) {
      opts.reporterOptions.split(",").forEach(function (opt) {
        var L = opt.split("=");
        if (L.length > 2 || L.length === 0) {
          throw new Error("invalid reporter option '" + opt + "'");
        } else if (L.length === 2) {
          reporterOptions[L[0]] = L[1];
        } else {
          reporterOptions[L[0]] = true;
        }
      });
    }

    // custom reporters
    mocha.reporter(opts.reporter, reporterOptions);
    return mocha;
  }
}

module.exports = MochaFactory;

'use strict';

let Base = require('mocha/lib/reporters/base');
let ms = require('mocha/lib/ms');
let event = require('../event');
let AssertionFailedError = require('../assert/error');
let output = require('../output');
let tests = [];
let currentTest;
var cursor = Base.cursor;
var color = Base.color;

class Cli extends Base {
  constructor(runner, opts) {
    super(runner);
    let level = 0;
    if (opts.steps) level = 1;
    if (opts.debug) level = 2;
    if (opts.verbose) level = 3;

    output.process(opts.child);
    output.level(level);
    output.print('CodeceptJS v' + require('../codecept').version());
    output.print(`Using test root "${global.codecept_dir}"`);

    let showSteps = level >= 1;

    let indents = 0;
    function indent() {
      return Array(indents).join('  ');
    }

    runner.on('start', function () {
      console.log();
    });

    runner.on('suite', function (suite) {
      output.suite.started(suite);
    });

    runner.on('fail', function (test, err) {
      if (showSteps && test.steps) {
        return output.scenario.failed(test);
      }
      cursor.CR();
      output.test.failed(test);
    });

    runner.on('pending', function (test) {
      cursor.CR();
      output.test.skipped(test);
    });

    runner.on('pass', function (test) {
      if (showSteps && test.steps) {
        return output.scenario.passed(test);
      }
      cursor.CR();
      output.test.passed(test);
    });

    if (showSteps) {
      runner.on('test', function (test) {
        if (test.steps) {
          output.test.started(test);
        }
      });

      event.dispatcher.on(event.step.started, function (step) {
        output.step(step, global.suiteDetails);
      });
    }

    runner.on('end', this.result.bind(this));
  }

  result() {
    let stats = this.stats;
    console.log();

    // passes
    if (stats.failures) {
      output.print('-- FAILURES:');
    }

    // failures
    if (stats.failures) {
      // append step traces
      this.failures.forEach((test) => {
        let err = test.err;
        if (err instanceof AssertionFailedError) {
          err.message = err.cliMessage();

          // remove error message from stacktrace
          var lines = err.stack.split('\n');
          lines.splice(0, 1);
          err.stack = lines.join('\n');
        }
        if (output.level() < 3) {
          err.stack += `\n\nRun with --verbose flag to see NodeJS stacktrace`;
        }
      });

      Base.list(this.failures);
      console.log();
    }

    output.result(stats.passes, stats.failures, stats.pending, ms(stats.duration));
  }
}
module.exports = function (runner, opts) {
  return new Cli(runner, opts);
};

var events = require('events');
var dispatcher = new events.EventEmitter();
var log = require('./output').log;

module.exports = {
  dispatcher,
  test: {
    started: 'test.start',
    before: 'test.before',
    after: 'test.after',
    passed: 'test.passed',
    failed: 'test.failed',
  },
  suite: {
    before: 'suite.before',
    after: 'suite.after'
  },
  step: {
    before: 'step.before',
    started: 'step.start',
    after: 'step.after'
  },
  all: {
    before: 'global.before',
    after: 'global.after',
    result: 'global.result'
  },

  emit: function (event, param) {
    var msg = 'Emitted | ' + event;
    if (param && param.toString()) {
      msg += ` (${param.toString()})`;
    }
    log(msg);
    try {
      this.dispatcher.emit.apply(this.dispatcher, arguments);
    } catch (err) {
      log(`Error processing ${event} event:`);
      log(err.stack);
    }
  },

  // for testing only!
  cleanDispatcher: () => {
    var event;
    for (event in this.test) {
      this.dispatcher.removeAllListeners(this.test[event]);
    }
    for (event in this.suite) {
      this.dispatcher.removeAllListeners(this.test[event]);
    }
    for (event in this.step) {
      this.dispatcher.removeAllListeners(this.test[event]);
    }
    for (event in this.all) {
      this.dispatcher.removeAllListeners(this.test[event]);
    }
  }
};

'use strict';

let colors = require('chalk');
let symbols = require('mocha/lib/reporters/base').symbols;

let styles = {
  error: colors.bgRed.white.bold,
  success: colors.bgGreen.white.bold,
  scenario: colors.magenta.bold,
  basic: colors.white,
  debug: colors.cyan,
  log: colors.grey
};

let outputLevel = 0;
let outputProcess = '';

module.exports = {
  colors,
  styles,
  print,
  stepShift: 0,

  /**
   * Set or return current verbosity level
   */
  level: (level) => {
    if (level) outputLevel = level;
    return outputLevel;
  },

  /**
   * Print information for a process
   * Used in multiple-run
   */
  process: (process) => {
    if (process) outputProcess = `[${process}]`;
    return outputProcess;
  },

  /**
   * Print information in --debug mode
   */
  debug: (msg) => {
    if (outputLevel >= 2) print(' '.repeat(this.stepShift), styles.debug("> " + msg));
  },

  /**
   * Print information in --verbose mode
   */
  log: (msg) => {
    if (outputLevel >= 3) print(' '.repeat(this.stepShift), styles.log("  " + msg));
  },

  /**
   * Print error
   */
  error: (msg) => {
    print(styles.error(msg));
  },

  /**
   * Print a successful message
   */
  success: (msg) => {
    print(styles.success(msg));
  },

  /**
   * Print a step
   */
  step: function (step) {
    if (outputLevel === 0) return;
    if (!step) return;
    let sym = process.platform === 'win32' ? '*' : 'â€¢';
    print(' '.repeat(this.stepShift), `${sym} ${step.toString()}`);
  },

  suite: {
    started: (suite) => {
      if (!suite.title) return;
      print(colors.bold(suite.title) + ' --');
    }
  },

  test: {
    started: (test) => {
      print(` ${colors.magenta.bold(test.title)}`);
    },
    passed: (test) => {
      print(` ${colors.green.bold(symbols.ok)} ${test.title} ${colors.grey('in ' + test.duration + 'ms')}`);
    },
    failed: (test) => {
      print(` ${colors.red.bold(symbols.err)} ${test.title} ${colors.grey('in ' + test.duration + 'ms')}`);
    },
    skipped: (test) => {
      print(` ${colors.yellow.bold('S')} ${test.title}`);
    }
  },

  scenario: {
    started: (test) => {

    },
    passed: (test) => {
      print(' ' + colors.green.bold(`${symbols.ok} OK`) + ' ' + colors.grey(`in ${test.duration}ms`));
      print();
    },
    failed: (test) => {
      print(' ' + colors.red.bold(`${symbols.err} FAILED`) + ' ' + colors.grey(`in ${test.duration}ms`));
      print();
    }
  },

  say: (message) => {
    if (outputLevel >= 1) print(`   ${colors.cyan.bold(message)}`);
  },

  result: (passed, failed, skipped, duration) => {
    let style = colors.bgGreen;
    let msg = ` ${passed || 0} passed`;
    let status = style.bold(`  OK `);
    if (failed) {
      style = style.bgRed;
      status = style.bold(`  FAIL `);
      msg += `, ${failed} failed`;
    }
    status += style.grey(` |`);

    if (skipped) {
      if (!failed) style = style.bgYellow;
      msg += `, ${skipped} skipped`;
    }
    msg += "  ";
    print(status + style(msg) + colors.grey(` // ${duration}`));
  }
};

function print(...msg) {
  if (outputProcess) {
    msg.unshift(outputProcess);
  }
  console.log.apply(this, msg);
}

function ind() {
  return "  ";
}

'use strict';
let subs = require('../utils').template;

/**
 * Assertion errors, can provide a detailed error messages.
 *
 * inspect() and cliMessage() added to display errors with params.
 */
function AssertionFailedError(params, template) {
  this.params = params;
  this.template = template;
  // this.message = "AssertionFailedError";
  let stack = new Error().stack;
  // this.showDiff = true;
  stack = stack.split("\n").filter((line) => {
    // @todo cut assert things nicer
    return line.indexOf('lib/assert') < 0;
  });
  this.stack = stack.join("\n");
  this.showDiff = true;

  this.actual = this.params.actual;
  this.expected = this.params.expected;

  this.inspect = () => {
    let params = this.params || {};
    let msg = params.customMessage || '';
    return msg + subs(this.template, params);
  };

  this.cliMessage = () => {
    return this.inspect();
  };
}

AssertionFailedError.prototype = Object.create(Error.prototype);

module.exports = AssertionFailedError;

'use strict';
let fs = require('fs');
let path = require('path');
let isFile = require('./utils').isFile;
let fileExists = require('./utils').fileExists;
const deepMerge = require('./utils').deepMerge;

let defaultConfig = {
  output: './_output',
  helpers: {},
  include: {},
  mocha: {},
  bootstrap: null,
  teardown: null,
  hooks: []
};

let config = {};

/**
 * Current configuration
 */
class Config {

  /**
   * Create a config with default options
   *
   * @param {*} newConfig
   */
  static create(newConfig) {
    return config = deepMerge(defaultConfig, newConfig);
  }

  /**
   * Load config from a file.
   * If js file provided: require it and get .config key
   * If json file provided: load and parse JSON
   * If directory provided:
   * * try to load `codecept.conf.js` from it
   * * try to load `codecept.json` from it
   * If none of above: fail.
   *
   * @param {*} configFile
   */
  static load(configFile) {
    configFile = path.resolve(configFile || '.');


    if (!fileExists(configFile)) {
      throw new Error(`Config file ${configFile} does not exist. Execute 'codeceptjs init' to create config`);
    }

    // is config file
    if (isFile(configFile)) {
      return loadConfigFile(configFile);
    }

    // is path to directory
    let jsConfig = path.join(configFile, 'codecept.conf.js');
    if (isFile(jsConfig)) {
      return loadConfigFile(jsConfig);
    }

    let jsonConfig = path.join(configFile, 'codecept.json');
    if (isFile(jsonConfig)) {
      return loadConfigFile(jsonConfig);
    }

    throw new Error(`Can not load config from ${jsConfig} or ${jsonConfig}\nCodeceptJS is not initialized in this dir. Execute 'codeceptjs init' to start`);
  }

  /**
   * Get current config.
   */
  static get() {
    return config;
  }

  /**
   * Appends values to current config
   *
   * @param {*} additionalConfig
   */
  static append(additionalConfig) {
    return config = deepMerge(config, additionalConfig);
  }

  /**
   * Resets config to default
   */
  static reset() {
    return config = defaultConfig;
  }
}

module.exports = Config;

function loadConfigFile(configFile) {
  // .conf.js config file
  if (path.extname(configFile) == '.js') {
    return Config.create(require(configFile).config);
  }

  // json config provided
  if (path.extname(configFile) == '.json') {
    return Config.create(JSON.parse(fs.readFileSync(configFile, 'utf8')));
  }
  throw new Error(`Config file ${configFile} can't be loaded`);
}

'use strict';

let getParamNames = require('./utils').getParamNames;
let fsPath = require('path');
let fileExists = require('./utils').fileExists;

module.exports = function (hook, done, stage) {
  stage = stage || 'bootstrap';
  if (typeof hook === 'string' && fileExists(fsPath.join(codecept_dir, hook))) {
    var callable = require(fsPath.join(codecept_dir, hook));
    if (typeof callable === 'function') {
      callSync(callable, done);
      return;
    }
    if (typeof callable === 'object' && callable[stage]) {
      callSync(callable[stage], done);
      return;
    }
  } else if (typeof hook === 'function') {
    callSync(hook, done);
    return;
  }
  // if async - call done
  if (done) done();
};

function callSync(callable, done) {
  if (isAsync(callable)) {
    callable(done);
  } else {
    callable();
    if (done) done();
  }
}

function isAsync(fn) {
  let params = getParamNames(fn);
  return params && params.length;
}

'use strict';

let promise, oldPromise;
let running = false;
let errFn;
let next;
let queueId = 0;
let sessionId = null;

let log = require('./output').log;
let tasks = [];

/**
 * Singleton object to record all test steps as promises and run them in chain.
 */
module.exports = {

  /**
   * Start recording promises
   *
   * @api
   */
  start() {
    running = true;
    errFn = null;
    this.reset();
  },

  /**
   * Add error handler to catch rejected promises
   *
   * @api
   * @param {*} fn
   */
  errHandler(fn) {
    errFn = fn;
  },

  /**
   * Stops current promise chain, calls `catch`.
   * Resets recorder to initial state.
   *
   * @api
   */
  reset() {
    if (promise && running) this.catch();
    queueId++;
    sessionId = null;
    log(currentQueue() + `Starting recording promises`);
    promise = Promise.resolve();
    oldPromise = null;
    tasks = [];
    this.session.running = false;
  },

  session: {
    running: false,

    start(name) {
      log(currentQueue() + `Starting <${name}> session`);
      tasks.push('--->');
      oldPromise = promise;
      this.running = true;
      sessionId = name;
      promise = Promise.resolve();
    },

    restore() {
      tasks.push('<---');
      log(currentQueue() + `Starting <${this.name}> session`);
      this.running = false;
      sessionId = null;
      promise = promise.then(() => oldPromise);
    },

    catch(errFn) {
      promise = promise.catch(errFn);
    }

  },

  /**
   * Adds a promise to a chain.
   * Promise description should be passed as first parameter.
   *
   * @param {*} taskName
   * @param {*} fn
   * @param {*} force
   */
  add(taskName, fn, force) {
    if (typeof taskName === "function") {
      fn = taskName;
      taskName = fn.toString();
    }
    if (!running && !force) {
      return;
    }
    tasks.push(taskName);
    log(currentQueue() + `Queued | ${taskName}`);
    return promise = promise.then(fn);
  },

  catch(customErrFn) {
    return promise = promise.catch((err) => {
      log(currentQueue() + `Error | ${err}`);
      if (!(err instanceof Error)) { // strange things may happen
        err = new Error('[Wrapped Error] ' + JSON.stringify(err)); // we should be prepared for them
      }
      if (customErrFn) {
        customErrFn(err);
      } else if (errFn) {
        errFn(err);
      }
      this.stop();
    });
  },

  /**
   * Adds a promise which throws an error into a chain
   *
   * @api
   * @param {*} err
   */
  throw(err) {
    return this.add('throw error ' + err, function () {
      throw err;
    });
  },

  /**
   * Stops recording promises
   * @api
   */
  stop() {
    log(currentQueue() + `Stopping recording promises`);
    var err = new Error();
    running = false;
  },

  /**
   * Get latest promise in chain.
   *
   * @api
   */
  promise() {
    return promise;
  },

  /**
   * Get a list of all chained tasks
   */
  scheduled() {
    return tasks.join("\n");
  },

  /**
   * Get a state of current queue and tasks
   */
  toString() {
    return `Queue: ${currentQueue()}\n\nTasks: ${this.scheduled()}`;
  }

};

function currentQueue() {
  let session = '';
  if (sessionId) session = `<${sessionId}> `;
  return `[${queueId}] ${session}`;
}

'use strict';
let Step = require('./step');
let container = require('./container');
let methodsOfObject = require('./utils').methodsOfObject;
let recorder = require('./recorder');
let event = require('./event');
let output = require('./output');

/**
 * Fetches all methods from all enabled helpers,
 * and makes them available to use from I. object
 * Wraps helper methods into promises.
 */
module.exports = function (obj) {
  obj = obj || {};

  let helpers = container.helpers();

  // add methods from enabled helpers
  Object.keys(helpers)
    .map((key) => helpers[key])
    .forEach((helper) => {
      methodsOfObject(helper, 'Helper')
      .filter((method) => {
        return method !== 'constructor' && method[0] !== '_';
      })
      .forEach((action) => {
        let actionAlias = container.translation().actionAliasFor(action);

        obj[action] = obj[actionAlias] = function () {
          let step = new Step(helper, action);
          if (container.translation().loaded) {
            step.name = actionAlias;
            step.actor = container.translation().I;
          }
          // add methods to promise chain
          return recordStep(step, Array.prototype.slice.call(arguments));
        };
      });
    });

  // add print comment method`
  obj.say = (msg) => recorder.add(`say ${msg}`, () => output.say(msg));

  return obj;
};

function recordStep(step, args) {
  step.status = 'queued';
  step.setArguments(args);

  // run async before step hooks
  event.emit(event.step.before, step);

  let task = `${step.name}: ${step.humanizeArgs()}`;
  let val;

  // run step inside promise
  recorder.add(task, () => {
    event.emit(event.step.started, step);
    val = step.run.apply(step, args);
  });

  // run async after step hooks
  event.emit(event.step.after, step);

  // ensure step result is passed to next step
  recorder.add('return step result', () => val);
  return recorder.promise();
}

'use strict';

/**
 * Each command in test executed through `I.` object is wrapped in Step.
 * Step allows logging executed commands and triggers hook before and after step execution.
 */
class Step {

  constructor(helper, name) {
    this.actor = 'I'; // I = actor
    this.helper = helper; // corresponding helper
    this.name = name; // name of a step console
    this.helperMethod = name; // helper method
    this.status = 'pending';
    this.prefix = this.suffix = '';
    Error.captureStackTrace(this);
    this.args = [];
  }

  setArguments(args) {
    this.args = args;
  }

  run() {
    this.args = Array.prototype.slice.call(arguments);
    let result;
    try {
      result = this.helper[this.helperMethod].apply(this.helper, this.args);
      this.status = 'success';
    } catch (err) {
      this.status = 'failed';
      throw err;
    }
    return result;
  }

  humanize() {
    return this.name
    // insert a space before all caps
    .replace(/([A-Z])/g, ' $1')
    // _ chars to spaces
    .replace('_', ' ')
    // uppercase the first character
    .replace(/^(.)|\s(.)/g, function ($1) {
      return $1.toLowerCase();
    });
  }

  humanizeArgs() {
    return this.args.map((arg) => {
      if (typeof arg === "string") {
        return `"${arg}"`;
      } else if (Array.isArray(arg)) {
        return `[${arg.toString()}]`;
      } else if (typeof arg === "function") {
        return "function()";
      } else if (typeof arg === "object") {
        return JSON.stringify(arg);
      } else if (arg.toString) {
        return arg.toString();
      }
      return arg;
    }).join(', ');
  }

  line() {
    var lines = this.stack.split('\n');
    // 3rd line is line where step has been called in test
    if (lines[3]) return lines[3].trim();
    return '';
  }

  toString() {
    return `${this.prefix}${this.actor} ${this.humanize()} ${this.humanizeArgs()}${this.suffix}`;
  }

  toCode() {
    return `${this.prefix}${this.actor}.${this.name}(${this.humanizeArgs()})${this.suffix}`;
  }

}

module.exports = Step;

