const path = require('path');
const fs = require('fs');
const Config = require('../config');
const Codecept = require('../codecept');
const output = require('../output');

module.exports = function (test, options) {
  // registering options globally to use in config
  process.profile = options.profile;
  let configFile = options.config;
  let codecept, outputDir;

  let testRoot = getTestRoot(configFile);
  let config = getConfig(configFile);
  if (options.override) {
    config = Config.append(JSON.parse(options.override));
  }

  if (!fileExists(outputDir = path.join(testRoot, config.output))) {
    output.print('creating output directory: ' + outputDir);
    fs.mkdirSync(outputDir);
  }

  try {
    codecept = new Codecept(config, options);
    codecept.init(testRoot, function (err) {
      if (err) throw new Error('Error while running bootstrap file :' + err);
      codecept.loadTests();
      codecept.run(test);
    });
  } catch (err) {
    output.print('');
    output.error(err.message);
    output.print('');
    output.print(output.colors.grey(err.stack.replace(err.message, '')));
    process.exit(1);
  }
};

'use strict';
let getConfig = require('./utils').getConfig;
let getTestRoot = require('./utils').getTestRoot;
let fail = require('./utils').fail;
let deepMerge = require('./utils').deepMerge;
let Codecept = require('../codecept');
let Config = require('../config');
let fork = require('child_process').fork;
let output = require('../output');
const path = require('path');
const runner = path.join(__dirname, '/../../bin/codecept');
let config, childOpts = {};
const copyOptions = ['steps', 'reporter', 'verbose', 'config', 'reporter-options', 'grep', 'fgrep'];

// codeceptjs run:multiple smoke:chrome regression:firefox - will launch smoke suite in chrome and regression in firefox
// codeceptjs run:multiple smoke:chrome regression - will launch smoke suite in chrome and regression in firefox and chrome
// codeceptjs run:multiple all - will launch all suites
// codeceptjs run:multiple smoke regression'

let suiteId = 1;

module.exports = function (suites, options) {
  // registering options globally to use in config
  process.profile = options.profile;
  let configFile = options.config;
  let codecept;

  let testRoot = getTestRoot(configFile);
  config = getConfig(configFile);

  let configMultiple = config.multiple;

  if (!configMultiple) {
    fail(`Multiple suites not configured, add "multiple": { /../ } section to config`);
  }

  // copy opts to run
  Object.keys(options)
    .filter((key) => copyOptions.indexOf(key) > -1)
    .forEach((key) => childOpts[key] = options[key]);

  if (options.all) {
    suites = Object.keys(configMultiple);
  }
  if (!suites.length) {
    fail('No suites provided. Use --all option to run all configured suites');
  }

  //iterate options
  suites.forEach(function (suite) {

    // get suites
    suite = suite.split(':');

    let suiteName = suite[0];
    let browser = suite[1];
    let suiteConf = configMultiple[suiteName];

    if (!suiteConf) {
      throw new Error(`Suite ${suiteName} was not configured in "multiple" section of config`);
    }

    runSuite(suiteName, suiteConf, browser);
  });
};

function runSuite(suite, suiteConf, browser) {
  let suiteBrowsers = suiteConf.browsers.map((b) => typeof b === 'object' ? b.browser : b);

  if (!browser) {
    // run all browsers in this suite
    suiteBrowsers.forEach((b) => runSuite(suite, suiteConf, b));
    return;
  }

  // clone config
  let overriddenConfig = Object.assign({}, config);

  if (suiteBrowsers.indexOf(browser) === -1) {
    throw new Error(`Browser ${browser} not found in multiple suite "${suite}" config`);
  }

  let browserConfig = suiteConf.browsers
    .map((b) => typeof b === 'object' ? b : { browser: b })
    .filter((b) => b.browser == browser)[0];

  for (let key in browserConfig) {
    overriddenConfig.helpers = replaceValue(overriddenConfig.helpers, key, browserConfig[key]);
  }

  let outputDir = suite + JSON.stringify(browserConfig).replace(/[^\d\w]+/g, '_') + suiteId;

  // tweaking default output directories and for mochawesome
  overriddenConfig = replaceValue(overriddenConfig, 'output', path.join(config.output, outputDir));
  overriddenConfig = replaceValue(overriddenConfig, 'reportDir', path.join(config.output, outputDir));

  // override grep param and collect all params
  let params = ['run',
    '--child', `${suiteId++}.${suite}:${browser}`,
    '--override', JSON.stringify(overriddenConfig)
  ];

  Object.keys(childOpts).forEach((key) => {
    params.push('--' + key);
    if (childOpts[key] !== true) params.push(childOpts[key]);
  });

  if (suiteConf.grep) {
    params.push('--grep');
    params.push(suiteConf.grep);
  }

  fork(runner, params, {stdio: [0, 1, 2, 'ipc']})
    .on('exit', (code) => code === 0 ? null : process.exitCode = 1)
    .on('error', (err) => process.exitCode = 1);
}


/**
* search key in object recursive and replace value in it
*/
function replaceValue(obj, key, value) {
  if (!obj) return;
  if (obj instanceof Array) {
    for (var i in obj) {
      replaceValue(obj[i], key, value);
    }
  }
  if (obj[key]) obj[key] = value;
  if (typeof obj === "object" && obj !== null) {
    var children = Object.keys(obj);
    for (i = 0; i < children.length; i++) {
      replaceValue(obj[children[i]], key, value);
    }
  }
  return obj;
}

'use strict';

let output = require('./output');
let AssertionFailedError = require('./assert/error');
let subs = require('./utils').template;

/**
 * Abstract assertion class introduced for more verbose and customizable messages.
 *
 * Should be easy to extend and use in custom helper classes.
 *
 * Both positive and negative assertions can be used with `assert` and `negate`.
 *
 * Should be used as:
 *
 * ```js
 * let comparator = (a, b) => a === b;
 * let assertion = new Assertion(compare);
 * assertion.assert(1,1);
 * assertion.negate(1,2);
 * ```
 *
 * Additional parameters can be passed to constructor or to assert/negate methods
 * to get more customizable exception messages.
 *
 */
class Assertion {

  constructor(comparator, params) {
    this.comparator = comparator;
    this.params = params || {};
    this.params.customMessage = '';
  }

  /**
   * Performs positive assertion.
   * Fails if comparator function with provided arguments returns false
   */
  assert() {
    this.addAssertParams.apply(this, arguments);
    let result = this.comparator.apply(this.params, arguments);
    if (result) return; // should increase global assetion counter
    throw this.getFailedAssertion();
  }

  /**
   * Performs negative assertion.
   * Fails if comparator function with provided arguments returns true
   */
  negate() {
    this.addAssertParams.apply(this, arguments);
    let result = this.comparator.apply(this.params, arguments);
    if (!result) return; // should increase global assetion counter
    throw this.getFailedNegation();
  }

  /**
   * Used to save additional parameters passed to assert/negate
   */
  addAssertParams() {}

  getException() {
    return new AssertionFailedError(this.params, '');
  }

  getFailedNegation() {
    let err = this.getException();
    err.params.type = 'not ' + err.params.type;
    return err;
  }

  getFailedAssertion() {
    return this.getException();
  }

}

module.exports = Assertion;

'use strict';
let event = require('./event');
let container = require('./container');
let recorder = require('./recorder');
let getParamNames = require('./utils').getParamNames;

/**
 * Wraps test function, injects support objects from container,
 * starts promise chain with recorder, performs before/after hooks
 * through event system.
 */
module.exports.test = (test) => {
  let testFn = test.fn;
  if (!testFn) {
    return test;
  }

  test.steps = [];

  test.fn = function (done) {
    recorder.errHandler(function (err) {
      recorder.session.start('teardown');
      event.emit(event.test.failed, test, err);
      recorder.add(() => done(err));
    });

    try {
      event.emit(event.test.started, test);
      let res = testFn.apply(test, getInjectedArguments(testFn));
      if (isGenerator(testFn)) {
        try {
          res.next(); // running test
        } catch (err) {
          event.emit(event.test.failed, test, err);
          done(err);
          return test;
        }
        recorder.catch(); // catching possible errors in promises
        let resumeTest = function () {
          recorder.add('create new promises queue for generator', function (data) {
            recorder.session.start('generator'); // creating a new promise chain
            try {
              let resume = res.next(data);
              if (resume.done) {
                recorder.add('fire test.passed', () => event.emit(event.test.passed, test));
                recorder.add('finish generator with no error', () => done()); // finish him
              } else {
                resumeTest();
              }
            } catch (err) {
              recorder.throw(err); // catch error in test and rethrow into promise
            }
            recorder.catch(); // handle all errors in promise chain
          });
        };
        resumeTest();
      }
    } catch (err) {
      recorder.throw(err);
    } finally {
      if (!isGenerator(testFn)) {
        recorder.add('fire test.passed', () => event.emit(event.test.passed, test));
        recorder.add('finish test', () => done());
        recorder.catch();
      }
    }
  };
  return test;
};

/**
 * Injects arguments to function from controller
 */
module.exports.injected = function (fn, suite) {
  return function () {
    try {
      fn.apply(this, getInjectedArguments(fn));
    } catch (err) {
      recorder.throw(err);
    }
    recorder.catch((err) => {
      event.emit(event.test.failed, suite, err); // emit
      throw err;
    });
    return recorder.promise();
  };
};

/**
 * Starts promise chain, so helpers could enqueue their hooks
 */
module.exports.setup = function () {
  recorder.start();
  event.emit(event.test.before);
};

module.exports.teardown = function () {
  event.emit(event.test.after);
};

module.exports.suiteSetup = function () {
  recorder.start();
  event.emit(event.suite.before);
};

module.exports.suiteTeardown = function () {
  recorder.start();
  event.emit(event.suite.after);
};

function isGenerator(fn) {
  return fn.constructor.name === 'GeneratorFunction';
}

function getInjectedArguments(fn) {
  let testArguments = [];
  let params = getParamNames(fn) || [];
  let objects = container.support();
  for (var key in params) {
    let obj = params[key];
    if (!objects[obj]) {
      throw new Error(`Object of type ${obj} is not defined in container`);
    }
    testArguments.push(container.support(obj));
  }
  return testArguments;
}

'use strict';
let Assertion = require('../assert');
let AssertionFailedError = require('./error');
let template = require('../utils').template;
let output = require('../output');

class EmptinessAssertion extends Assertion {

  constructor(params) {
    super(function (value) {
      if (Array.isArray(value)) {
        return value.length === 0;
      }
      return !value;
    }, params);
    this.params.type = 'to be empty';
  }

  getException() {
    if (Array.isArray(this.params.value)) {
      this.params.value = '[' + this.params.value.join(', ') + ']';
    }

    let err = new AssertionFailedError(this.params, "{{customMessage}}expected {{subject}} '{{value}}' {{type}}");

    err.cliMessage = () => {
      let msg = err.template
        .replace('{{value}}', output.colors.bold('{{value}}'))
        .replace('{{subject}}', output.colors.bold('{{subject}}'));
      return template(msg, this.params);
    };
    return err;
  }


  addAssertParams() {
    this.params.value = this.params.actual = arguments[0];
    this.params.expected = [];
    this.params.customMessage = arguments[1] ? arguments[1] + "\n\n" : '';
  }
}

module.exports = {
  Assertion: EmptinessAssertion,
  empty: (subject) => {
    return new EmptinessAssertion({subject});
  }
};

'use strict';
let Assertion = require('../assert');
let AssertionFailedError = require('./error');
let template = require('../utils').template;
let output = require('../output');

class EqualityAssertion extends Assertion {

  constructor(params) {
    let comparator = function (a, b) {
      return a === b;
    };
    super(comparator, params);
    this.params.type = 'to equal';
  }

  getException() {
    let params = this.params;
    params.jar = template(params.jar, params);
    let err = new AssertionFailedError(params, "{{customMessage}}expected {{jar}} '{{expected}}' {{type}} '{{actual}}'");
    err.showDiff = false;
    err.cliMessage = () => {
      let msg = err.template
        .replace('{{jar}}', output.colors.bold('{{jar}}'));
      return template(msg, this.params);
    };
    return err;
  }

  addAssertParams() {
    this.params.expected = arguments[0];
    this.params.actual = arguments[1];
    this.params.customMessage = arguments[2] ? arguments[2] + "\n\n" : '';
  }
}

module.exports = {
  Assertion: EqualityAssertion,
  equals: (jar) => {
    return new EqualityAssertion({ jar });
  },
  urlEquals: (baseUrl) => {
    let assert = new EqualityAssertion({ jar: 'url of current page' });
    assert.comparator = function (expected, actual) {
      if (expected.indexOf('http') !== 0) {
        actual = actual.slice(actual.indexOf(baseUrl) + baseUrl.length);
      }
      return actual === expected;
    };
    return assert;
  },
  fileEquals: (file) => {
    return new EqualityAssertion({ file, jar: "contents of {{file}}" }
  );
  }
};

'use strict';
let Assertion = require('../assert');
let AssertionFailedError = require('./error');
let template = require('../utils').template;
let output = require('../output');

const MAX_LINES = 10;


class InclusionAssertion extends Assertion {

  constructor(params) {
    params.jar = params.jar || 'string';
    let comparator = function (needle, haystack) {
      if (Array.isArray(haystack)) {
        return haystack.filter(part => part.indexOf(needle) >= 0).length > 0;
      }
      return haystack.indexOf(needle) >= 0;
    };
    super(comparator, params);
    this.params.type = 'to include';
  }

  getException() {
    let params = this.params;
    params.jar = template(params.jar, params);
    let err = new AssertionFailedError(params, '{{customMessage}}expected {{jar}} {{type}} "{{needle}}"');
    err.expected = params.needle;
    err.actual = params.haystack;
    if (Array.isArray(this.params.haystack)) {
      this.params.haystack = this.params.haystack.join("\n___(next element)___\n");
    }
    err.cliMessage = function () {
      let msg = this.template
        .replace('{{jar}}', output.colors.bold('{{jar}}'))
        .replace('{{needle}}', output.colors.bold('{{needle}}'));
      return template(msg, this.params);
    };
    return err;
  }

  getFailedAssertion() {
    let err = this.getException();
    let lines = this.params.haystack.split("\n");
    if (lines.length > MAX_LINES) {
      let more = lines.length - MAX_LINES;
      err.actual = lines.slice(0, MAX_LINES).join("\n") + `\n--( ${more} lines more )---`;
    }
    return err;
  }

  getFailedNegation() {
    this.params.type = 'not to include';
    let err = this.getException();
    let pattern = new RegExp("^.*?\n?^.*?\n?^.*?" + escapeRegExp(this.params.needle) + ".*?$\n?.*$\n?.*$", "m");
    let matched = this.params.haystack.match(pattern);
    if (!matched) return err;
    err.actual = matched[0].replace(this.params.needle, output.colors.bold(this.params.needle));
    err.actual = `------\n${err.actual}\n------`;
    return err;
  }

  addAssertParams() {
    this.params.needle = arguments[0];
    this.params.haystack = arguments[1];
    this.params.customMessage = arguments[2] ? arguments[2] + "\n\n" : '';
  }
}

module.exports = {
  Assertion: InclusionAssertion,
  includes: (needleType) => {
    needleType = needleType || 'string';
    return new InclusionAssertion({jar: needleType});
  },
  fileIncludes: (file) => {
    return new InclusionAssertion({ file, jar: "file {{file}}" }
      );
  }
};

function escapeRegExp(str) {
  return str.replace(/[\-\[\]\/\{\}\(\)\*\+\?\.\\\^\$\|]/g, "\\$&");
}

'use strict';
let Assertion = require('../assert');
let AssertionFailedError = require('./error');
let template = require('../utils').template;
let output = require('../output');

class TruthAssertion extends Assertion {

  constructor(params) {
    super(function (value) {
      if (Array.isArray(value)) {
        return value.filter((val) => !!val).length > 0;
      }
      return !!value;
    }, params);
    this.params.type = this.params.type || 'to be true';
  }

  getException() {
    let err = new AssertionFailedError(this.params, "{{customMessage}}expected {{subject}} {{type}}");
    err.cliMessage = () => {
      let msg = err.template
        .replace('{{subject}}', output.colors.bold('{{subject}}'));
      return template(msg, this.params);
    };
    return err;
  }

  addAssertParams() {
    this.params.value = this.params.actual = arguments[0];
    this.params.expected = true;
    this.params.customMessage = arguments[1] ? arguments[1] + "\n\n" : '';
  }
}

module.exports = {
  Assertion: TruthAssertion,
  truth: (subject, type) => {
    return new TruthAssertion({subject, type});
  }
};

'use strict';
let Helper = require('../helper');
let fileExists = require('../utils').fileExists;
let fileIncludes = require('../assert/include').fileIncludes;
let fileEquals = require('../assert/equal').fileEquals;
let assert = require('assert');
let path = require('path');
let fs = require('fs');

/**
 * Helper for testing filesystem.
 * Can be easily used to check file structures:
 *
 * ```js
 * I.amInPath('test');
 * I.seeFile('codecept.json');
 * I.seeInThisFile('FileSystem');
 * I.dontSeeInThisFile("WebDriverIO");
 * ```
 */
class FileSystem extends Helper {

  _before() {
    this.dir = global.codecept_dir;
    this.file = null;
    this.debugSection('Dir', this.dir);
  }

  /**
   * Enters a directory In local filesystem.
   * Starts from a current directory
   */
  amInPath(openPath) {
    this.dir = path.join(global.codecept_dir, openPath);
    this.debugSection('Dir', this.dir);
  }

  /**
   * Writes test to file
   */
  writeToFile(name, text) {
    fs.writeFileSync(path.join(this.dir, name), text);
  }

  /**
   * Checks that file exists
   */
  seeFile(name) {
    this.file = path.join(this.dir, name);
    this.debugSection('File', this.file);
    assert.ok(fileExists(this.file), `File ${name} not found in ${this.dir}`);
  }

  /**
   * Checks that file found by `seeFile` includes a text.
   */
  seeInThisFile(text, encoding) {
    let content = getFileContents(this.file, encoding);
    fileIncludes(this.file).assert(text, content);
  }

  /**
   * Checks that file found by `seeFile` doesn't include text.
   */
  dontSeeInThisFile(text, encoding) {
    let content = getFileContents(this.file, encoding);
    fileIncludes(this.file).negate(text, content);
  }

  /**
   * Checks that contents of file found by `seeFile` equal to text.
   */
  seeFileContentsEqual(text, encoding) {
    let content = getFileContents(this.file, encoding);
    fileEquals(this.file).assert(text, content);
  }

  /**
   * Checks that contents of file found by `seeFile` doesn't equal to text.
   */
  dontSeeFileContentsEqual(text, encoding) {
    let content = getFileContents(this.file, encoding);
    fileEquals(this.file).negate(text, content);
  }

}

function getFileContents(file, encoding) {
  if (!file) assert.fail(`No files were opened, please use seeFile action`);
  encoding = encoding || 'utf8';
  return fs.readFileSync(file, 'utf8');

}

module.exports = FileSystem;

'use strict';
const requireg = require('requireg');
const Helper = require('../helper');
const stringIncludes = require('../assert/include').includes;
const urlEquals = require('../assert/equal').urlEquals;
const equals = require('../assert/equal').equals;
const empty = require('../assert/empty').empty;
const truth = require('../assert/truth').truth;
const xpathLocator = require('../utils').xpathLocator;
const fileExists = require('../utils').fileExists;
const co = require('co');
const path = require('path');

let specialKeys = {
  Backspace: '\u0008',
  Enter: '\u000d',
  Delete: '\u007f'
};

/**
 * Nightmare helper wraps [Nightmare](https://github.com/segmentio/nightmare) library to provide
 * fastest headless testing using Electron engine. Unlike Selenium-based drivers this uses
 * Chromium-based browser with Electron with lots of client side scripts, thus should be less stable and
 * less trusted.
 *
 * Requires `nightmare` and `nightmare-upload` packages to be installed.
 *
 * ### Configuration
 *
 * This helper should be configured in codecept.json
 *
 * * `url` - base url of website to be tested
 * * `restart` (optional, default: true) - restart browser between tests.
 * * `keepCookies` (optional, default: false)  - keep cookies between tests when `restart` set to false.
 * * `waitForAction`: (optional) how long to wait after click, doubleClick or PressKey actions in ms. Default: 500
 * * `waitForTimeout`: (optional) default wait* timeout
 * * `windowSize`: (optional) default window size. Set a dimension like `640x480`.
 *
 * + options from [Nightmare configuration](https://github.com/segmentio/nightmare#api)
 *
 */
class Nightmare extends Helper {

  constructor(config) {
    super(config);

    // set defaults
    this.options = {
      waitForAction: 500,
      waitForTimeout: 1000,
      rootElement: 'body',
      restart: true,
      keepCookies: false,
      js_errors: null
    };

    // override defaults with config
    Object.assign(this.options, config);

    this.context = this.options.rootElement;
    this.options.waitForTimeout;
  }

  static _config() {
    return [
      { name: 'url', message: "Base url of site to be tested", default: 'http://localhost' },
    ];
  }

  static _checkRequirements() {
    try {
      requireg("nightmare");
      requireg("nightmare-upload");
    } catch(e) {
      return ["nightmare", "nightmare-upload"];
    }
  }

  _init() {
    this.Nightmare = requireg('nightmare');

    require('nightmare-upload')(this.Nightmare);

    this.Nightmare.action('findElements', function (locator, contextEl, done) {

      if (!done) {
        done = contextEl;
        contextEl = null;
      }

      let by = Object.keys(locator)[0];
      let value = locator[by];

      this.evaluate_now(function (by, locator, contextEl) {
        return window.codeceptjs.findAndStoreElements(by, locator);
      }, done, by, value, contextEl);
    });

    this.Nightmare.action('findElement', function (locator, contextEl, done) {
      if (!done) {
        done = contextEl;
        contextEl = null;
      }

      let by = Object.keys(locator)[0];
      let value = locator[by];

      this.evaluate_now(function (by, locator, contextEl) {
        let res = window.codeceptjs.findAndStoreElement(by, locator);
        if (res === null) {
          throw new Error(`Element ${locator} couldn't be located by ${by}`);
        }
        return res;
      }, done, by, value, contextEl);
    });

    this.Nightmare.action('asyncScript', function () {
      let args = Array.prototype.slice.call(arguments);
      let done = args.pop();
      args = args.splice(1, 0, done);
      this.evaluate_now.apply(this, args);
    });

    this.Nightmare.action('enterText', function (el, text, clean, done) {

      let child = this.child;
      let typeFn = () => child.call('type', text, done);

      this.evaluate_now(function (el, clean) {
        var el = window.codeceptjs.fetchElement(el);
        if (clean) el.value = '';
        el.focus();
      }, () => {
        if (clean) return typeFn();
        child.call('pressKey', 'End', typeFn); // type End before
      }, el, clean);
    });

    this.Nightmare.action('pressKey', function (ns, options, parent, win, renderer, done) {
      parent.respondTo('pressKey', function (ch, done) {
        win.webContents.sendInputEvent({
          type: 'keyDown',
          keyCode: ch
        });

        win.webContents.sendInputEvent({
          type: 'char',
          keyCode: ch
        });

        win.webContents.sendInputEvent({
          type: 'keyUp',
          keyCode: ch
        });
        done();
      });
      done();
    }, function (key, done) {
