function proceedSee(assertType, text, context) {
  let description;
  if (!context) {
    context = this.context;
    if (this.context === 'body') {
      description = 'web page';
    } else {
      description = 'current context ' + this.context;
    }
  } else {
    description = 'element ' + context;
  }
  return this.browser.getText(withStrictLocator(context)).then(function (source) {
    return stringIncludes(description)[assertType](text, source);
  });
}

function findClickable(client, locator) {
  if (typeof locator === 'object') return client.elements(withStrictLocator(locator));
  if (isCSSorXPathLocator(locator)) return client.elements(locator);

  let literal = xpathLocator.literal(locator);

  let narrowLocator = xpathLocator.combine([
    `.//a[normalize-space(.)=${literal}]`,
    `.//button[normalize-space(.)=${literal}]`,
    `.//a/img[normalize-space(@alt)=${literal}]/ancestor::a`,
    `.//input[./@type = 'submit' or ./@type = 'image' or ./@type = 'button'][normalize-space(@value)=${literal}]`
  ]);
  return client.elements(narrowLocator).then(function (els) {
    if (els.value.length) {
      return els;
    }
    let wideLocator = xpathLocator.combine([
      `.//a[./@href][((contains(normalize-space(string(.)), ${literal})) or .//img[contains(./@alt, ${literal})])]`,
      `.//input[./@type = 'submit' or ./@type = 'image' or ./@type = 'button'][contains(./@value, ${literal})]`,
      `.//input[./@type = 'image'][contains(./@alt, ${literal})]`,
      `.//button[contains(normalize-space(string(.)), ${literal})]`,
      `.//input[./@type = 'submit' or ./@type = 'image' or ./@type = 'button'][./@name = ${literal}]`,
      `.//button[./@name = ${literal}]`
    ]);
    return client.elements(wideLocator).then(function (els) {
      if (els.value.length) {
        return els;
      }
      return client.elements(locator); // by css or xpath
    });
  });
}

function findFields(client, locator) {
  if (typeof locator === 'object') return client.elements(withStrictLocator(locator));
  if (isCSSorXPathLocator(locator)) return client.elements(locator);

  let literal = xpathLocator.literal(locator);
  let byText = xpathLocator.combine([
    `.//*[self::input | self::textarea | self::select][not(./@type = 'submit' or ./@type = 'image' or ./@type = 'hidden')][(((./@name = ${literal}) or ./@id = //label[contains(normalize-space(string(.)), ${literal})]/@for) or ./@placeholder = ${literal})]`,
    `.//label[contains(normalize-space(string(.)), ${literal})]//.//*[self::input | self::textarea | self::select][not(./@type = 'submit' or ./@type = 'image' or ./@type = 'hidden')]`
  ]);
  return client.elements(byText).then((els) => {
    if (els.value.length) return els;
    let byName = `.//*[self::input | self::textarea | self::select][@name = ${literal}]`;
    return client.elements(byName).then((els) => {
      if (els.value.length) return els;
      return client.elements(locator); // by css or xpath
    });
  });
}

function proceedSeeField(assertType, field, value) {
  return findFields(this.browser, field).then(function (res) {
    if (!res.value || res.value.length === 0) {
      throw new Error(`Field ${field} not found by name|text|CSS|XPath`);
    }

    var proceedMultiple = (fields) => {
      let commands = [];
      fields.forEach((el) => commands.push(this.elementIdSelected(el.ELEMENT)));
      this.unify(commands).then((res) => {
        commands = [];
        fields.forEach((el) => {
          if (el.value === false) return;
          commands.push(this.elementIdAttribute(el.ELEMENT, 'value'));
        });
        this.unify(commands, {
          extractValue: true
        }).then((val) => {
          return stringIncludes('fields by ' + field)[assertType](value, val);
        });
      });
    };

    var proceedSingle = (el) => {
      return this.elementIdAttribute(el.ELEMENT, 'value').then((res) => {
        return stringIncludes('fields by ' + field)[assertType](value, res.value);
      });
    };

    return this.elementIdName(res.value[0].ELEMENT).then((tag) => {
      if (tag.value == 'select') {
        return proceedMultiple(res.value);
      }

      if (tag.value == 'input') {
        return this.elementIdAttribute(res.value[0].ELEMENT, 'type').then((type) => {
          if (type.value == 'checkbox' || type.value == 'radio') {
            return proceedMultiple(res.value);
          }
          return proceedSingle(res.value[0]);
        });
      }
      return proceedSingle(res.value[0]);
    });
  });
}

function proceedSeeCheckbox(assertType, field) {
  return findFields(this.browser, field).then(function (res) {
    if (!res.value || res.value.length === 0) {
      throw new Error(`Field ${field} not found by name|text|CSS|XPath`);
    }
    let commands = [];
    res.value.forEach((el) => commands.push(this.elementIdSelected(el.ELEMENT)));
    return this.unify(commands, {
      extractValue: true
    }).then((selected) => {
      return truth(`checkable field ${field}`, 'to be checked')[assertType](selected);
    });
  });
}

function findCheckable(client, locator) {
  if (typeof locator === 'object') return client.elements(withStrictLocator(locator));
  if (isCSSorXPathLocator(locator)) return client.elements(locator);

  let literal = xpathLocator.literal(locator);
  let byText = xpathLocator.combine([
    `.//input[@type = 'checkbox' or @type = 'radio'][(@id = //label[contains(normalize-space(string(.)), ${literal})]/@for) or @placeholder = ${literal}]`,
    `.//label[contains(normalize-space(string(.)), ${literal})]//input[@type = 'radio' or @type = 'checkbox']`
  ]);
  return client.elements(byText).then(function (els) {
    if (els.value.length) return els;
    let byName = `.//input[@type = 'checkbox' or @type = 'radio'][@name = ${literal}]`;
    return client.elements(byName).then(function (els) {
      if (els.value.length) return els;
      return client.elements(locator); // by css or xpath
    });
  });
}

function isCSSorXPathLocator(locator) {
  if (locator[0] === '#' || locator[0] === '.') {
    return true;
  }
  if (locator.substr(0, 2) === '//') {
    return true;
  }
  return false;
}

function withStrictLocator(locator) {
  if (!locator) return null;
  if (typeof locator !== 'object') return locator;
  let key = Object.keys(locator)[0];
  let value = locator[key];

  locator.toString = () => `{${key}: '${value}'}`;

  switch (key) {
  case 'by':
  case 'xpath':
  case 'css':
    return value;
  case 'id':
    return '#' + value;
  case 'name':
    return `[name="${value}"]`;
  }
}

function isFrameLocator(locator) {
  if (typeof locator !== 'object') return false;
  let key = Object.keys(locator)[0];
  if (key !== 'frame') return false;
  return locator[key];
}

module.exports = WebDriverIO;

/**
 * Module dependencies.
 */

var Suite = require('mocha/lib/suite');
var Test = require('mocha/lib/test');
var event = require('../event');
var scenario = require('../scenario');
var recorder = require('../recorder');
var escapeRe = require('escape-string-regexp');

/**
 * Codecept-style interface:
 *
 * Feature('login')
 * Scenario('login as regular user', (I) {
 *   I.fillField();
 *   I.click()
 *   I.see('Hello, '+data.login);
 * });
 *
 * @param {Suite} suite Root suite.
 */
module.exports = function (suite) {
  var suites = [suite];
  suite.timeout(0);

  suite.on('pre-require', function (context, file, mocha) {
    var common = require('mocha/lib/interfaces/common')(suites, context);

    // create dispatcher

    context.BeforeAll = common.before;
    context.AfterAll = common.after;

    context.run = mocha.options.delay && common.runWithSuite(suite);
    /**
     * Describe a "suite" with the given `title`
     * and callback `fn` containing nested suites
     * and/or tests.
     */

    context.Feature = function (title, opts) {
      if (suites.length > 1) {
        suites.shift();
      }
      if (!opts) opts = {};
      var suite = Suite.create(suites[0], title);
      suite.timeout(0);

      if (opts.retries) suite.retries(opts.retries);
      if (opts.timeout) suite.timeout(opts.timeout);

      suite.file = file;
      suites.unshift(suite);
      suite.beforeEach('codeceptjs.before', scenario.setup);
      suite.afterEach('finialize codeceptjs', scenario.teardown);

      suite.beforeAll('codeceptjs.beforeSuite', scenario.suiteSetup);
      suite.afterAll('codeceptjs.afterSuite', scenario.suiteTeardown);

      return suite;
    };

    context.BeforeSuite = function (fn) {
      suites[0].beforeAll('BeforeSuite', scenario.injected(fn, suites[0]));
    };

    context.AfterSuite = function (fn) {
      suites[0].afterAll('AfterSuite', scenario.injected(fn, suites[0]));
    };

    context.Background = context.Before = function (fn) {
      suites[0].beforeEach('Before', scenario.injected(fn, suites[0]));
    };

    context.After = function (fn) {
      suites[0].afterEach('After', scenario.injected(fn, suites[0]));
    };

    /**
     * Describe a specification or test-case
     * with the given `title` and callback `fn`
     * acting as a thunk.
     */
    context.Scenario = function (title, opts, fn) {
      if (typeof opts === 'function' && !fn) {
        fn = opts;
        opts = {};
      }

      var suite = suites[0];
      if (suite.pending) {
        fn = null;
      }
      var test = new Test(title, fn);
      test.fullTitle = () => `${suite.title}: ${title}`;
      test.file = file;
      test.async = true;
      test.timeout(0);

      if (opts.retries) test.retries(opts.retries);
      if (opts.timeout) test.timeout(opts.timeout);

      suite.addTest(scenario.test(test));
      return test;
    };

    /**
     * Exclusive test-case.
     */
    context.Scenario.only = function (title, fn) {
      var test = context.Scenario(title, fn);
      var reString = '^' + escapeRe(test.fullTitle()) + '$';
      mocha.grep(new RegExp(reString));
    };

    /**
     * Pending test case.
     */
    context.xScenario = context.Scenario.skip = function (title) {
      context.Scenario(title, {});
    };
  });
};

'use strict';

const event = require('../event');

module.exports = function () {

  let failed = false;

  let failedTests = [];

  event.dispatcher.on(event.test.failed, function (testOrSuite) {
    // NOTE When an error happens in one of the hooks (BeforeAll/BeforeEach...) the event object
    // is a suite and not a test
    failedTests.push(testOrSuite.fullTitle());
  });

  // if test was successful after retries
  event.dispatcher.on(event.test.passed, function (testOrSuite) {
    // NOTE When an error happens in one of the hooks (BeforeAll/BeforeEach...) the event object
    // is a suite and not a test
    failedTests = failedTests.filter((failed) => testOrSuite.fullTitle() !== failed);
  });

  event.dispatcher.on(event.all.result, function () {
    if (failedTests.length) {
      process.exitCode = 1;
    }
  });
};

'use strict';

const event = require('../event');
const container = require('../container');
const recorder = require('../recorder');
const debug = require('../output').debug;

/**
 * Enable Helpers to listen to test events
 */
module.exports = function () {
  let helpers = container.helpers();

  const runHelpersHook = (hook, param) => {
    Object.keys(helpers).forEach((key) => {
      helpers[key][hook](param);
    });
  };

  const runAsyncHelpersHook = (hook, param, force) => {
    Object.keys(helpers).forEach((key) => {
      if (!helpers[key][hook]) return;
      recorder.add(`hook ${key}.${hook}()`, () => helpers[key][hook](param), force);
    });
  };

  event.dispatcher.on(event.all.before, function () {
    runHelpersHook('_init');
  });

  event.dispatcher.on(event.suite.before, function (suite) {
    runAsyncHelpersHook('_beforeSuite', null, true);
  });

  event.dispatcher.on(event.suite.after, function (suite) {
    runAsyncHelpersHook('_afterSuite', null, true);
  });

  event.dispatcher.on(event.test.started, function (test) {
    runHelpersHook('_test', test);
    recorder.catch((e) => debug(e));
  });

  event.dispatcher.on(event.test.before, function () {
    runAsyncHelpersHook('_before');
  });

  event.dispatcher.on(event.test.failed, function (test) {
    runAsyncHelpersHook('_failed', test, true);
    // should not fail test execution, so errors should be catched
    recorder.catch((e) => debug(e));
  });

  event.dispatcher.on(event.test.after, function (test) {
    runAsyncHelpersHook('_after', {}, true);
  });

  event.dispatcher.on(event.step.before, function (step) {
    runAsyncHelpersHook('_beforeStep', step);
  });

  event.dispatcher.on(event.step.after, function (step) {
    runAsyncHelpersHook('_afterStep', step);
  });
};

'use strict';
const event = require('../event');

let currentTest;
let steps;

/**
 * Register steps inside tests
 */
module.exports = function () {

  event.dispatcher.on(event.test.started, function (test) {
    currentTest = test;
    currentTest.steps = [];
  });

  event.dispatcher.on(event.test.after, function () {
    currentTest = null;
  });

  event.dispatcher.on(event.test.failed, function (test) {
    if (!currentTest) return;
    // last step is failing step
    if (!currentTest.steps.length) return;
    currentTest.steps.slice(-1)[0].status = 'failed';
  });

  event.dispatcher.on(event.step.before, function (step) {
    if (!currentTest || !currentTest.steps) return;
    currentTest.steps.push(step);
  });
};

'use strict';

const output = require('../output');
const event = require('../event');
const AssertionFailedError = require('../assert/error');
const ucfirst = require('../utils').ucfirst;

/**
 * Register stack trace for scenarios
 */
module.exports = function () {

  event.dispatcher.on(event.test.failed, function (test, err) {
    let msg = err.message;
    if (err instanceof AssertionFailedError) {
      msg = err.message = err.inspect();
    }
    let steps = test.steps;
    if (steps && steps.length) {
      let scenarioTrace = "";
      steps.reverse().forEach((step, i) => {
        let line = `- ${step.toCode()} ${step.line()}`;
        // if (step.status === 'failed') line = '' + line;
        scenarioTrace += "\n" + line;
      });
      msg += `\n\nScenario Steps:\n${scenarioTrace}\n\n`;
    }

    if (output.level() < 3) {
      err.stack = ''; // hide internal error stack trace in non-verbose mode
    }

    err.stack = msg + err.stack;
    test.err = err;
  });

};
